    state = {
        editMode: false,
    };

    activateEditMode = () => {
        this.setState({
            editMode: true,
        })
    };
    deActivateEditMode = () => {
        this.setState({
            editMode: false
        })
        this.props.updateStatus(status)
    };

    render() {
        return (
            <div>
                {!this.state.editMode &&
                    <div>
                        <span onDoubleClick={this.activateEditMode}>
                            {this.props.status}
                        </span>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input ref={this.statusInputRef} autoFocus={true} onBlur={this.deActivateEditMode} value={this.props.status} />
                    </div>
                }
            </div>
        )
    }
    
};
